import React, { useState, useEffect, useRef } from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate, Link } from 'react-router-dom';
import './App.css';
import { FiMenu, FiChevronLeft, FiMessageSquare, FiClock, FiUser, FiPlus, FiX, FiArrowRight, FiLogOut, FiUserCheck, FiCamera, FiMail, FiLock, FiUpload, FiEye, FiEyeOff, FiEdit2 } from 'react-icons/fi';
import EnhancedProfile from './EnhancedProfile';

const LOGO_SRC = process.env.PUBLIC_URL + '/newlogo.jpg';
const API_URL = process.env.REACT_APP_API_URL || 'http://localhost:5000';

// Local Authentication System
const LOCAL_STORAGE_KEYS = {
  USERS: 'mbot_users_db',
  CURRENT_USER: 'mbot_current_user',
  USER_SESSIONS: 'mbot_user_sessions'
};

const LOCAL_CREDENTIALS_KEY = 'mbot_local_credentials';

const simpleEncrypt = (text) => btoa(text);
const simpleDecrypt = (encoded) => {
  try {
    return atob(encoded);
  } catch {
    return null;
  }
};

// Mobile Header Component
const MobileHeader = ({ user, onProfileClick, isSidebarOpen, setIsSidebarOpen }) => {
  const [isScrolled, setIsScrolled] = useState(false);

  useEffect(() => {
    const handleScroll = () => {
      setIsScrolled(window.scrollY > 10);
    };
    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  const toggleSidebar = () => {
    setIsSidebarOpen(!isSidebarOpen);
  };

  const getUserInitials = () => {
    if (user?.screen_name) {
      return user.screen_name.split(' ').map(n => n[0]).join('').toUpperCase();
    }
    return user?.email ? user.email[0].toUpperCase() : 'M';
  };

  return (
    <header className={`mobile-header ${isScrolled ? 'scrolled' : ''}`}>
      <div className="header-left">
        <button 
          className={`hamburger-menu ${isSidebarOpen ? 'active' : ''}`}
          onClick={toggleSidebar}
          aria-label="Toggle menu"
        >
          <span className="hamburger-line"></span>
          <span className="hamburger-line"></span>
          <span className="hamburger-line"></span>
        </button>
        <a href="#" className="header-logo" onClick={(e) => e.preventDefault()}>
          <img src={LOGO_SRC} alt="M Logo" />
          <h1>M</h1>
        </a>
      </div>
      <div className="header-right">
        <button 
          className="header-profile-btn"
          onClick={onProfileClick}
          aria-label="Profile"
        >
          {getUserInitials()}
        </button>
      </div>
    </header>
  );
};

// Bottom Navigation Component
const BottomNavigation = ({ currentPage, setCurrentPage }) => {
  const navItems = [
    { id: 'chat', label: 'Chat', icon: FiMessageSquare },
    { id: 'dashboard', label: 'Dashboard', icon: FiClock },
    { id: 'profile', label: 'Profile', icon: FiUser },
    { id: 'feedback', label: 'Feedback', icon: FiEdit2 }
  ];

  return (
    <nav className="bottom-nav">
      {navItems.map(({ id, label, icon: Icon }) => (
        <button
          key={id}
          className={`nav-item-mobile ${currentPage === id ? 'active' : ''}`}
          onClick={() => setCurrentPage(id)}
          aria-label={label}
        >
          <Icon className="nav-icon-mobile" />
          <span className="nav-label-mobile">{label}</span>
        </button>
      ))}
    </nav>
  );
};

function saveCredentialsLocally(email, password) {
  try {
    const credentials = JSON.parse(localStorage.getItem(LOCAL_CREDENTIALS_KEY) || '{}');
    credentials[email] = simpleEncrypt(password);
    localStorage.setItem(LOCAL_CREDENTIALS_KEY, JSON.stringify(credentials));
  } catch (e) {
    // fail silently
  }
}

function verifyStoredCredentials(email, password) {
  try {
    const credentials = JSON.parse(localStorage.getItem(LOCAL_CREDENTIALS_KEY) || '{}');
    if (!credentials[email]) return false;
    return simpleDecrypt(credentials[email]) === password;
  } catch (e) {
    return false;
  }
}

class LocalAuthSystem {
  constructor() {
    this.initializeDatabase();
  }

  initializeDatabase() {
    if (!localStorage.getItem(LOCAL_STORAGE_KEYS.USERS)) {
      localStorage.setItem(LOCAL_STORAGE_KEYS.USERS, JSON.stringify({}));
    }
    if (!localStorage.getItem(LOCAL_STORAGE_KEYS.USER_SESSIONS)) {
      localStorage.setItem(LOCAL_STORAGE_KEYS.USER_SESSIONS, JSON.stringify({}));
    }
  }

  getAllUsers() {
    try {
      const users = localStorage.getItem(LOCAL_STORAGE_KEYS.USERS);
      return users ? JSON.parse(users) : {};
    } catch (error) {
      console.error('Error reading users database:', error);
      return {};
    }
  }

  saveUsersDatabase(users) {
    try {
      localStorage.setItem(LOCAL_STORAGE_KEYS.USERS, JSON.stringify(users));
      return true;
    } catch (error) {
      console.error('Error saving users database:', error);
      return false;
    }
  }

  registerUser(email, password, name) {
    const users = this.getAllUsers();

    if (users[email]) {
      return { success: false, error: 'User already exists' };
    }

    const userId = 'user_' + Date.now() + '_' + Math.random().toString(36).substr(2, 9);
    const newUser = {
      id: userId,
      user_id: userId,
      email: email,
      password: simpleEncrypt(password),
      name: name,
      screen_name: name,
      created_at: new Date().toISOString(),
      last_login: null,
      profile: {
        pronouns: '',
        identity_goals: '',
        focus_areas: []
      }
    };

    users[email] = newUser;

    if (this.saveUsersDatabase(users)) {
      return { 
        success: true, 
        user: { ...newUser, password: undefined },
        userId: userId
      };
    } else {
      return { success: false, error: 'Failed to save user data' };
    }
  }

  loginUser(email, password) {
    const users = this.getAllUsers();
    const user = users[email];

    if (!user) {
      return { success: false, error: 'User not found' };
    }

    const storedPassword = simpleDecrypt(user.password);
    if (storedPassword !== password) {
      return { success: false, error: 'Invalid email or password' };
    }

    user.last_login = new Date().toISOString();
    users[email] = user;
    this.saveUsersDatabase(users);

    const session = this.createSession(user);

    return { 
      success: true, 
      user: { ...user, password: undefined },
      session: session
    };
  }

  createSession(user) {
    const sessionId = 'session_' + Date.now() + '_' + Math.random().toString(36).substr(2, 9);
    const session = {
      sessionId: sessionId,
      userId: user.id,
      email: user.email,
      created_at: new Date().toISOString(),
      expires_at: new Date(Date.now() + 7 * 24 * 60 * 60 * 1000).toISOString()
    };

    try {
      const sessions = JSON.parse(localStorage.getItem(LOCAL_STORAGE_KEYS.USER_SESSIONS) || '{}');
      sessions[sessionId] = session;
      localStorage.setItem(LOCAL_STORAGE_KEYS.USER_SESSIONS, JSON.stringify(sessions));

      localStorage.setItem(LOCAL_STORAGE_KEYS.CURRENT_USER, JSON.stringify({
        ...user,
        password: undefined,
        sessionId: sessionId
      }));

      return session;
    } catch (error) {
      console.error('Error creating session:', error);
      return null;
    }
  }

  getCurrentUser() {
    try {
      const currentUser = localStorage.getItem(LOCAL_STORAGE_KEYS.CURRENT_USER);
      if (!currentUser) {
        return null;
      }
      return JSON.parse(currentUser);
    } catch (error) {
      console.error('Error getting current user:', error);
      return null;
    }
  }

  logout() {
    try {
      localStorage.removeItem(LOCAL_STORAGE_KEYS.CURRENT_USER);
      localStorage.removeItem('token');
      localStorage.removeItem('user_id');
      return { success: true };
    } catch (error) {
      console.error('Error during logout:', error);
      return { success: false, error: 'Logout failed' };
    }
  }
}

