// GoogleOAuth.js - Add this as a separate component
const GoogleOAuth = ({ onSuccess, onError, loading, setLoading }) => {
  useEffect(() => {
    // Load Google OAuth script
    const script = document.createElement('script');
    script.src = 'https://accounts.google.com/gsi/client';
    script.async = true;
    script.defer = true;
    document.body.appendChild(script);

    script.onload = () => {
      if (window.google) {
        window.google.accounts.id.initialize({
          client_id: process.env.REACT_APP_GOOGLE_CLIENT_ID || 'your-google-client-id',
          callback: handleCredentialResponse,
          auto_select: false,
          cancel_on_tap_outside: false,
        });
      }
    };

    return () => {
      document.body.removeChild(script);
    };
    // eslint-disable-next-line
  }, []);

  const handleCredentialResponse = async (response) => {
    setLoading(true);
    try {
      // Decode JWT token to get user info
      const credential = response.credential;
      const payload = JSON.parse(atob(credential.split('.')[1]));

      const userInfo = {
        email: payload.email,
        name: payload.name,
        picture: payload.picture,
        given_name: payload.given_name,
        family_name: payload.family_name,
        email_verified: payload.email_verified
      };

      // Send to backend for authentication
      const apiResponse = await fetch(`${process.env.REACT_APP_API_URL || 'http://localhost:5000'}/api/google-login`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        credentials: 'include',
        body: JSON.stringify({
          token: credential,
          user_info: userInfo
        })
      });

      const data = await apiResponse.json();

      if (apiResponse.ok) {
        localStorage.setItem('token', data.access_token);
        localStorage.setItem('user_id', data.user_id);
        onSuccess(data);
      } else {
        onError(data.error || 'Google login failed');
      }
    } catch (error) {
      console.error('Google OAuth error:', error);
      onError('Google login failed. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  const handleGoogleLogin = () => {
    if (window.google) {
      window.google.accounts.id.prompt();
    } else {
      onError('Google OAuth not loaded. Please refresh and try again.');
    }
  };

  return (
    <button 
      type="button" 
      className="btn-google"
      onClick={handleGoogleLogin}
      disabled={loading}
      style={{ 
        marginBottom: '1.5rem',
        opacity: loading ? 0.6 : 1,
        cursor: loading ? 'not-allowed' : 'pointer'
      }}
    >
      <span style={{ fontSize: '18px' }}>🌐</span>
      {loading ? 'Connecting...' : 'Continue with Google'}
    </button>
  );
};

