// Enhanced Feedback Component
const Feedback = ({ user }) => {
  const [rating, setRating] = useState(0);
  const [feedback, setFeedback] = useState('');
  const [category, setCategory] = useState('General');
  const [submitted, setSubmitted] = useState(false);
  const [loading, setLoading] = useState(false);

  const handleRatingClick = (value) => {
    setRating(value);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);

    try {
      const response = await fetch(`${API_URL}/api/feedback`, {
        method: 'POST',
        headers: {
          'Authorization': `Bearer ${localStorage.getItem('token')}`,
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          rating,
          feedback,
          category
        })
      });

      if (response.ok) {
        setSubmitted(true);
        setRating(0);
        setFeedback('');
        setCategory('General');
      }
    } catch (err) {
      console.error('Error submitting feedback:', err);
      alert('Error submitting feedback. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  if (submitted) {
    return (
      <div className="feedback-container">
        <div style={{ 
          textAlign: 'center', 
          padding: '3rem',
          background: 'linear-gradient(135deg, var(--color-highlight) 0%, rgba(189, 162, 224, 0.8) 100%)',
          borderRadius: '2rem',
          color: 'var(--color-bg-primary)'
        }}>
          <div style={{ fontSize: '4rem', marginBottom: '1rem' }}>✨</div>
          <h2 style={{ marginBottom: '1rem' }}>Thank You!</h2>
          <p style={{ marginBottom: '2rem', opacity: 0.9 }}>
            Your feedback helps us improve M and create better experiences for everyone.
          </p>
          <button
            onClick={() => setSubmitted(false)}
            className="btn-primary"
            style={{ background: 'var(--color-brand-primary)' }}
          >
            Submit Another Response
          </button>
        </div>
      </div>
    );
  }

  return (
    <div className="feedback-container">
      <h2 className="feedback-title">How would you rate your experience with M?</h2>

      <div className="rating-container">
        <div className="stars">
          {[1, 2, 3, 4, 5].map((star) => (
            <button
              key={star}
              className={`star ${rating >= star ? 'active' : ''}`}
              onClick={() => handleRatingClick(star)}
            >
              ★
            </button>
          ))}
        </div>
        <div className="rating-labels" style={{ color: '#200125', fontWeight: 700, fontSize: 13, letterSpacing: '0.5px' }}>
          <span>Poor</span>
          <span>Fair</span>
          <span>Excellent</span>
        </div>
      </div>

      <form onSubmit={handleSubmit} style={{ width: '100%', display: 'flex', flexDirection: 'column', alignItems: 'center', maxWidth: 400, margin: '0 auto' }}>
        <div className="form-group" style={{ width: '100%', marginBottom: '1.5rem' }}>
          <label htmlFor="category" style={{ marginBottom: 8, color: '#fff', fontWeight: 600, fontSize: '1.1rem' }}>Category</label>
          <select
            id="category"
            value={category}
            onChange={e => setCategory(e.target.value)}
            style={{
              width: '100%',
              border: 'none',
              borderRadius: '1.2rem',
              padding: '0.8rem 1.2rem',
              fontSize: '1.1rem',
              background: '#fff',
              color: '#2d0036',
              marginBottom: 0,
            }}
          >
            <option value="General">General</option>
            <option value="Support">Support</option>
            <option value="Technical">Technical</option>
            <option value="Other">Other</option>
          </select>
        </div>
        <div className="form-group" style={{ width: '100%', marginBottom: '2rem' }}>
          <label htmlFor="feedback" style={{ marginBottom: 8, color: '#fff', fontWeight: 600, fontSize: '1.1rem' }}>Additional Comments (Optional)</label>
          <textarea
            id="feedback"
            value={feedback}
            onChange={(e) => setFeedback(e.target.value)}
            placeholder="Share your thoughts about your experience..."
            rows={5}
            style={{
              width: '100%',
              minHeight: 120,
              border: 'none',
              borderRadius: '1.5rem',
              padding: '1.2rem 1.5rem',
              fontSize: '1.15rem',
              background: '#fff',
              color: '#2d0036',
              boxShadow: '0 2px 12px rgba(32,1,37,0.10)',
              outline: 'none',
              display: 'block',
              transition: 'box-shadow 0.2s',
              textAlign: 'left',
              fontFamily: 'inherit',
              resize: 'vertical',
            }}
          />
        </div>
        <button
          type="submit"
          className={`btn-primary ${loading ? 'loading' : ''}`}
          disabled={rating === 0 || loading}
          style={{
            width: '100%',
            padding: '1.1rem 0',
            border: 'none',
            borderRadius: '1.5rem',
            background: (rating === 0 || loading) ? '#7fa88a' : 'var(--color-brand-primary)',
            color: '#fff',
            fontSize: '1.2rem',
            fontWeight: 600,
            cursor: (rating === 0 || loading) ? 'not-allowed' : 'pointer',
            boxShadow: '0 2px 8px rgba(32,1,37,0.08)',
            transition: 'background 0.2s, color 0.2s',
            marginTop: '0.5rem',
            display: 'block',
            textAlign: 'center',
          }}
        >
          {loading ? 'Submitting...' : 'Submit Feedback'}
        </button>
      </form>
    </div>
  );
};

// Main App Component with Enhanced State Management
const App = () => {
  const [user, setUser] = useState(null);
  const [currentPage, setCurrentPage] = useState('chat');
  const [loading, setLoading] = useState(true);
  const [isSidebarOpen, setIsSidebarOpen] = useState(window.innerWidth > 768);

  const [selectedSessionId, setSelectedSessionId] = useState(null);
  const [activeSessionId, setActiveSessionId] = useState(null);
  const [sessionUpdateTrigger, setSessionUpdateTrigger] = useState(0);

  useEffect(() => {
    const handleResize = () => {
      setIsSidebarOpen(window.innerWidth > 768);
    };
    window.addEventListener('resize', handleResize);
    return () => window.removeEventListener('resize', handleResize);
  }, []);

  useEffect(() => {
    checkAuthStatus();
  }, []);

  const checkAuthStatus = async () => {
    try {
      const token = localStorage.getItem('token');
      if (!token) {
        setLoading(false);
        return;
      }

      const response = await fetch(`${API_URL}/api/user`, {
        headers: {
          'Authorization': `Bearer ${token}`
        }
      });

      if (response.ok) {
        const userData = await response.json();
        setUser(userData);
      } else {
        localStorage.removeItem('token');
        localStorage.removeItem('user_id');
      }
    } catch (error) {
      console.error('Auth check failed:', error);
      localStorage.removeItem('token');
      localStorage.removeItem('user_id');
    } finally {
      setLoading(false);
    }
  };

  const handleLogin = (userData) => {
    setUser(userData);
  };

  const handleLogout = async () => {
    try {
      await fetch(`${API_URL}/api/logout`, {
        method: 'POST',
        credentials: 'include'
      });
    } catch (error) {
      console.error('Logout error:', error);
    }

    localStorage.removeItem('token');
    localStorage.removeItem('user_id');
    setUser(null);
    setCurrentPage('chat');
    setSelectedSessionId(null);
    setActiveSessionId(null);
  };

  const handleUpdateProfile = (profileData) => {
    setUser(prev => ({ ...prev, ...profileData }));
    triggerSessionUpdate();
  };

  const handleSelectSession = (sessionId) => {
    setSelectedSessionId(sessionId);
    setActiveSessionId(null);
  };

  const handleStartNewChat = () => {
    setSelectedSessionId(null);
    setActiveSessionId(null);
    triggerSessionUpdate();
  };

  const handleEndSession = (sessionId) => {
    setActiveSessionId(null);
    setSelectedSessionId(null);
    triggerSessionUpdate();
  };

  const triggerSessionUpdate = () => {
    setSessionUpdateTrigger(prev => prev + 1);
  };

  const handleProfileClick = () => {
    setCurrentPage('profile');
    setIsSidebarOpen(false);
  };

  if (loading) {
    return (
      <div className="app-loading">
        <div className="loading-spinner"></div>
        <p>Loading M...</p>
      </div>
    );
  }

  if (!user) {
    return <Login onLogin={handleLogin} />;
  }

  const renderPage = () => {
    switch (currentPage) {
      case 'chat':
        return (
          <Chat
            user={user}
            selectedSessionId={selectedSessionId}
            activeSessionId={activeSessionId}
            setActiveSessionId={setActiveSessionId}
            onEndSession={handleEndSession}
            onStartNewSession={handleStartNewChat}
            userProfile={user}
            onSessionUpdate={triggerSessionUpdate}
          />
        );
      case 'dashboard':
        return <Dashboard user={user} refresh={sessionUpdateTrigger} />;
      case 'profile':
        return <EnhancedProfile user={user} onUpdateProfile={handleUpdateProfile} />;
      case 'about':
        return <About />;
      case 'feedback':
        return <Feedback user={user} />;
      default:
        return <Navigate to="/chat" replace />;
    }
  };

  return (
    <div className="app">
      <Navigation 
        user={user} 
        setCurrentPage={setCurrentPage}
        currentPage={currentPage}
        onLogout={handleLogout}
        isSidebarOpen={isSidebarOpen}
        setIsSidebarOpen={setIsSidebarOpen}
        onSelectSession={handleSelectSession}
        selectedSessionId={selectedSessionId}
        onStartNewChat={handleStartNewChat}
        refreshTrigger={sessionUpdateTrigger}
      />
      <main className={`main-content${isSidebarOpen ? '' : ' full'}`}>
        {user.user_id && user.user_id.startsWith('local_user_') && (
          <div style={{
            background: 'var(--color-warning)',
            color: 'white',
            padding: '12px 16px',
            borderRadius: '12px',
            margin: '1rem',
            textAlign: 'center',
            fontWeight: '600',
            fontSize: '1.1rem',
            zIndex: 1000
          }}>
            You are using local-only login. Please sign in with a real account to access all features.
          </div>
        )}
        <div style={{ marginLeft: !isSidebarOpen ? 56 : 0, transition: 'margin-left 0.3s' }}>
          {renderPage()}
        </div>
      </main>
    </div>
  );
};

export default App;
