// 💬 Enhanced Chat Interface
const ChatInterface = ({ user, selectedSessionId, onSessionUpdate }) => {
  const [messages, setMessages] = useState([]);
  const [inputMessage, setInputMessage] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const [isTyping, setIsTyping] = useState(false);

  useEffect(() => {
    if (selectedSessionId) {
      fetchMessages();
    } else {
      setMessages([]);
    }
  }, [selectedSessionId]);

  const fetchMessages = async () => {
    if (!selectedSessionId) return;

    try {
      const response = await fetch(`/api/chat_messages?session_id=${selectedSessionId}`);
      if (response.ok) {
        const fetchedMessages = await response.json();
        setMessages(fetchedMessages || []);
      }
    } catch (error) {
      console.error('Failed to fetch messages:', error);
      toast.error('Failed to load messages');
    }
  };

  const sendMessage = async () => {
    if (!inputMessage.trim() || isLoading) return;

    const userMessage = {
      id: Date.now(),
      content: inputMessage,
      sender: 'user',
      timestamp: new Date().toISOString()
    };

    setMessages(prev => [...prev, userMessage]);
    setInputMessage('');
    setIsLoading(true);
    setIsTyping(true);

    try {
      const response = await fetch('/api/chat', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          user_id: user.user_id,
          message: inputMessage,
          session_id: selectedSessionId
        })
      });

      if (response.ok) {
        const data = await response.json();
        setIsTyping(false);

        const assistantMessage = {
          id: Date.now() + 1,
          content: data.response,
          sender: 'assistant',
          timestamp: new Date().toISOString()
        };

        setMessages(prev => [...prev, assistantMessage]);
        onSessionUpdate();
      } else {
        throw new Error('Failed to send message');
      }
    } catch (error) {
      console.error('Error sending message:', error);
      setIsTyping(false);
      toast.error('Failed to send message. Please try again.');
    } finally {
      setIsLoading(false);
    }
  };

  const handleKeyPress = (e) => {
    if (e.key === 'Enter' && !e.shiftKey) {
      e.preventDefault();
      sendMessage();
    }
  };

  // Auto-resize textarea
  const adjustTextareaHeight = (textarea) => {
    textarea.style.height = 'auto';
    textarea.style.height = Math.min(textarea.scrollHeight, 120) + 'px';
  };

  const handleInputChange = (e) => {
    setInputMessage(e.target.value);
    adjustTextareaHeight(e.target);
  };

  return (
    <motion.div 
      className="chat-container"
      variants={pageVariants}
      initial="initial"
      animate="in"
      exit="out"
      transition={pageTransition}
    >
      <div className="chat-messages">
        <AnimatePresence>
          {messages.map((message, index) => (
            <motion.div
              key={message.id || index}
              className={`message ${message.sender}`}
              initial={{ opacity: 0, y: 20 }}
              animate={{ opacity: 1, y: 0 }}
              exit={{ opacity: 0, y: -20 }}
              transition={{ duration: 0.3, delay: index * 0.1 }}
            >
              <div className="message-avatar">
                {message.sender === 'user' ? 
                  user?.username?.charAt(0).toUpperCase() || 'U' : 
                  '🤖'
                }
              </div>
              <motion.div 
                className="message-content"
                whileHover={{ scale: 1.02 }}
              >
                {message.content}
              </motion.div>
            </motion.div>
          ))}

          {isTyping && (
            <motion.div
              className="message assistant"
              initial={{ opacity: 0, y: 20 }}
              animate={{ opacity: 1, y: 0 }}
              exit={{ opacity: 0, y: -20 }}
            >
              <div className="message-avatar">🤖</div>
              <div className="message-content">
                <div className="typing-indicator">
                  <motion.span
                    animate={{ scale: [1, 1.2, 1] }}
                    transition={{ duration: 0.6, repeat: Infinity, delay: 0 }}
                    style={{ display: 'inline-block', width: '8px', height: '8px', background: 'var(--color-brand-primary)', borderRadius: '50%', margin: '0 2px' }}
                  />
                  <motion.span
                    animate={{ scale: [1, 1.2, 1] }}
                    transition={{ duration: 0.6, repeat: Infinity, delay: 0.2 }}
                    style={{ display: 'inline-block', width: '8px', height: '8px', background: 'var(--color-brand-primary)', borderRadius: '50%', margin: '0 2px' }}
                  />
                  <motion.span
                    animate={{ scale: [1, 1.2, 1] }}
                    transition={{ duration: 0.6, repeat: Infinity, delay: 0.4 }}
                    style={{ display: 'inline-block', width: '8px', height: '8px', background: 'var(--color-brand-primary)', borderRadius: '50%', margin: '0 2px' }}
                  />
                </div>
              </div>
            </motion.div>
          )}
        </AnimatePresence>
      </div>

      <motion.div 
        className="chat-input-container"
        initial={{ y: 20, opacity: 0 }}
        animate={{ y: 0, opacity: 1 }}
        transition={{ delay: 0.3 }}
      >
        <div className="chat-input-wrapper">
          <textarea
            className="chat-input"
            value={inputMessage}
            onChange={handleInputChange}
            onKeyPress={handleKeyPress}
            placeholder="Share your thoughts with M... 💭"
            disabled={isLoading}
            rows={1}
            style={{
              minHeight: '44px',
              maxHeight: '120px',
              resize: 'none',
              overflow: 'hidden'
            }}
          />
          <motion.button
            className="send-button"
            onClick={sendMessage}
            disabled={isLoading || !inputMessage.trim()}
            whileHover={{ scale: 1.05 }}
            whileTap={{ scale: 0.95 }}
          >
            {isLoading ? (
              <div className="loading-spinner" style={{ width: '20px', height: '20px' }} />
            ) : (
              <FiSend size={20} />
            )}
          </motion.button>
        </div>
      </motion.div>
    </motion.div>
  );
};

